var labelArray_village = [];
//添加社区标注
export function addLabel_village(map) {
    var pointArray = [
        new BMapGL.Point(121.041294, 31.302276),  //陈巷村(社区)
        new BMapGL.Point(121.032593, 31.339461),  //神童泾村
        new BMapGL.Point(121.084829, 31.333325),  //泗桥村
        new BMapGL.Point(121.064825, 31.323261),  //邹家角村
        new BMapGL.Point(121.052097, 31.342213),  //夏桥村
        new BMapGL.Point(121.046028, 31.328015),  //车塘村
        new BMapGL.Point(121.012559, 31.348808),  //合丰村
        new BMapGL.Point(121.035883, 31.318471),  //邵村社区
        new BMapGL.Point(121.055328, 31.315684)  //陆家村
    ];

    var optsArray = [{}, {}, {}, {}, {}, {}, {}, {}, {}, {}, {}];

    var contentArray = [
        "陈巷社区", "神童泾社区", "泗桥社区", "邹家角社区", "夏桥社区", "车塘社区", "合丰社区", "邵村社区", "陆家社区"];
    for (var i = 0; i < pointArray.length; i++) {
        optsArray[i].position = pointArray[i];
        labelArray_village[i] = new BMapGL.Label(contentArray[i], optsArray[i]);
        labelArray_village[i].setStyle({
            //color: "#00FFFF",
            color: "white",
            fontSize: "18px",
            height: "30px",
            lineHeight: "30px",
            textAlign: "center",
            //backgroundColor: "#333333",
            backgroundColor: "transparent",
            textShadow: "0 0 8px #000000",
            //writingMode: "vertical-lr",
            border: "0",
            //fontWeight: "bold",
            fontFamily: "微软雅黑",
            transform: 'translateX(-50%)'//label相对坐标点自动居中显示
            ////transform: 'rotate(45deg) translateX(-50%)'//label相对坐标点自动居中显示
        });
        if (contentArray[i] == "车塘村(社区)") {
            labelArray_village[i].setStyle({
                writingMode: "vertical-lr",//文字垂直
                transform: 'translateX(-20px) translateY(-40px)'//label相对坐标点自动居中显示
            });
        }
        else if (contentArray[i] == "夏桥村(社区)") {
            labelArray_village[i].setStyle({
                writingMode: "vertical-lr",//文字垂直
            });
        }
        else if (contentArray[i] == "陆家村(社区)") {
            labelArray_village[i].setStyle({
                writingMode: "vertical-lr",//文字垂直
                transform: 'translateX(-20px) translateY(-20px)'
            });
        }
        else if (contentArray[i] == "邵村社区") {
            labelArray_village[i].setStyle({
                writingMode: "vertical-lr",//文字垂直
                transform: 'rotate(45deg) translateX(0px) translateY(0px)'
            });
        }
        else if (contentArray[i] == "泗桥村(社区)") {
            labelArray_village[i].setStyle({
                writingMode: "vertical-lr",//文字垂直
                transform: 'translateY(-20px)'
            });
        }
        else if (contentArray[i] == "邹家角村(社区)") {
            labelArray_village[i].setStyle({
                transform: 'rotate(-25deg) translateX(-30px) translateY(-30px)'
            });
        }
        labelArray_village[i].setTitle(contentArray[i]);
        
        // labelArray_village[i].addEventListener('click', function (e) {
        //     showPop("建筑垃圾");
        // });
        map.addOverlay(labelArray_village[i]);
    }
}
//删除社区标注
export function removeLabel_village() {
    var length = labelArray_village.length;
    for (var i = 0; i < length; i++) {
        map.removeOverlay(labelArray_village[i]);
    }
}



