//3D效果版本描边地图
export function getBoundary_village(map) {
    var areas = [];
    areas.push(chenxiang);
    areas.push(shentongjing);
    areas.push(siqiao);
    areas.push(zhoujiajiao);
    areas.push(xiaqiao);
    areas.push(chetang);
    areas.push(hefeng);
    areas.push(shaocun);
    areas.push(lujia);
    areas.push(lujia2);
    var pointArray = [];
    for (var i = 0; i < areas.length; i++) {
        var ply = new BMapGL.Polygon(areas[i], { strokeWeight: 4, strokeColor: "#ffffff" }); //建立多边形覆盖物
        ply.setFillColor("#54F8FD")   //设置多边形的填充颜色
        //ply.setFillOpacity(0.35);
        polygons_village.push(ply);  //加入多边形数组，以之后获取多边形边界点集
        //if (i == 3)
        //{
        //    ply.enableEditing();  //范围可编辑 【编辑的时候开启】
        //}
        map.addOverlay(ply);  //添加覆盖物
        pointArray = pointArray.concat(ply.getPath());
    }
    let res=map.getViewport(pointArray)
    console.log("getviewport");
    console.log(res);
    console.log(pointArray);
    
    //map.setViewport(pointArray);    //调整视野
    addLabel_village(map);
    return true;
}